package packswitch;

public class SwitchEvenOdd {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1=4;
		switch(num1%2) {
		case 0:
		    System.out.println(num1+" is an Even number");
		    break;
		case 1:
		    System.out.println(num1+" is an Odd number");
		    break;
		}
	}
}





